import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Parent() {
  const [reports, setReports] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/reports', {
      params: { userId: 'parent-id', role: 'parent' } // TODO: Get from login
    })
      .then(response => setReports(response.data))
      .catch(error => console.error('Error fetching reports:', error));
  }, []);

  return (
    <div style={{ padding: '50px' }}>
      <h1>Parent Dashboard</h1>
      {reports.map(report => (
        <div key={report._id} style={{ border: '1px solid gray', padding: '10px', margin: '10px 0' }}>
          <p>Grade: {report.grade}</p>
          <p>Attendance: {report.attendance ? 'Present' : 'Absent'}</p>
          <p>Comments: {report.comments}</p>
          <p>Date: {new Date(report.createdAt).toLocaleDateString()}</p>
        </div>
      ))}
    </div>
  );
}

export default Parent;
