import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../components/Auth';
import axios from 'axios';
import '../styles/Teacher.css';

function Teacher() {
  const { user } = useContext(AuthContext);
  const [form, setForm] = useState({
    student: '',
    subject: '',
    score: '',
    examType: 'opening-term',
    attendance: 'present',
    comment: '',
    editingId: null, // For editing
  });
  const [results, setResults] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const school = user?.school || '6826c6741e8bb0ac59a1bea9';
        console.log('Fetching results for school:', school);
        const response = await axios.get('http://localhost:5000/api/results', {
          params: { school },
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        console.log('Results received:', response.data);
        setResults(aggregateResults(response.data));
      } catch (error) {
        console.error('Fetch results error:', error);
        setError(error.response?.data?.error || 'Failed to load results');
      }
    };
    if (user) {
      fetchResults();
    }
  }, [user]);

  // Aggregate results by student
  const aggregateResults = (rawResults) => {
    const studentMap = {};
    rawResults.forEach((result) => {
      const studentId = result.student._id;
      if (!studentMap[studentId]) {
        studentMap[studentId] = {
          studentId,
          student: result.student.email,
          subjects: {},
          attendance: '',
          comment: '',
          teacher: result.teacher || user.email,
        };
      }
      if (result.type === 'exam') {
        if (!studentMap[studentId].subjects[result.subject]) {
          studentMap[studentId].subjects[result.subject] = {
            openingTerm: null,
            midTerm: null,
            endTerm: null,
            ids: {},
          };
        }
        studentMap[studentId].subjects[result.subject][result.examType] = result.score;
        studentMap[studentId].subjects[result.subject].ids[result.examType] = result._id;
      } else if (result.type === 'attendance') {
        studentMap[studentId].attendance = result.attendance;
        studentMap[studentId].attendanceId = result._id;
      } else if (result.type === 'comment') {
        studentMap[studentId].comment = result.comment;
        studentMap[studentId].commentId = result._id;
      }
    });

    // Calculate grades and ranks
    const students = Object.values(studentMap);
    students.forEach((student) => {
      Object.values(student.subjects).forEach((subject) => {
        const scores = [
          subject.openingTerm,
          subject.midTerm,
          subject.endTerm,
        ].filter((score) => score !== null);
        const average = scores.length
          ? (scores.reduce((sum, score) => sum + score, 0) / scores.length).toFixed(1)
          : null;
        subject.average = average;
        subject.grade = average ? calculateGrade(average, student.comment) : '-';
      });
      const totalAverage = Object.values(student.subjects)
        .map((s) => s.average)
        .filter((a) => a !== null)
        .reduce((sum, avg) => sum + parseFloat(avg), 0) /
        Object.values(student.subjects).length || null;
      student.totalAverage = totalAverage ? totalAverage.toFixed(1) : null;
    });

    // Sort by total average for ranking
    students.sort((a, b) => (b.totalAverage || 0) - (a.totalAverage || 0));
    students.forEach((student, index) => {
      student.rank = student.totalAverage ? index + 1 : '-';
    });

    return students;
  };

  // Calculate grade based on score and comment
  const calculateGrade = (score, comment) => {
    if (comment && comment.toLowerCase().includes('cheat')) return 'Y';
    if (score >= 80) return 'A';
    if (score >= 70) return 'B';
    if (score >= 60) return 'C';
    if (score >= 50) return 'D';
    return 'E';
  };

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      setError('');
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No token found. Please log in again.');
        return;
      }
      if (!form.student) {
        setError('Student ID is required.');
        return;
      }
      // Submit subject score (if provided)
      if (form.subject && form.score && form.examType) {
        const scorePayload = {
          student: form.student,
          type: 'exam',
          subject: form.subject,
          score: parseFloat(form.score),
          examType: form.examType,
          school: user?.school || '6826c6741e8bb0ac59a1bea9',
        };
        console.log('Submitting score:', scorePayload);
        if (form.editingId && form.type === 'exam') {
          await axios.put(`http://localhost:5000/api/results/${form.editingId}`, scorePayload, {
            headers: { Authorization: `Bearer ${token}` },
          });
        } else {
          await axios.post('http://localhost:5000/api/results', scorePayload, {
            headers: { Authorization: `Bearer ${token}` },
          });
        }
      }
      // Submit attendance (if not empty)
      if (form.attendance && !['exam', 'comment'].includes(form.type)) {
        const attendancePayload = {
          student: form.student,
          type: 'attendance',
          attendance: form.attendance,
          school: user?.school || '6826c6741e8bb0ac59a1bea9',
        };
        console.log('Submitting attendance:', attendancePayload);
        if (form.editingId && form.type === 'attendance') {
          await axios.put(`http://localhost:5000/api/results/${form.editingId}`, attendancePayload, {
            headers: { Authorization: `Bearer ${token}` },
          });
        } else {
          await axios.post('http://localhost:5000/api/results', attendancePayload, {
            headers: { Authorization: `Bearer ${token}` },
          });
        }
      }
      // Submit comment (if not empty)
      if (form.comment && !['exam', 'attendance'].includes(form.type)) {
        const commentPayload = {
          student: form.student,
          type: 'comment',
          comment: form.comment,
          school: user?.school || '6826c6741e8bb0ac59a1bea9',
        };
        console.log('Submitting comment:', commentPayload);
        if (form.editingId && form.type === 'comment') {
          await axios.put(`http://localhost:5000/api/results/${form.editingId}`, commentPayload, {
            headers: { Authorization: `Bearer ${token}` },
          });
        } else {
          await axios.post('http://localhost:5000/api/results', commentPayload, {
            headers: { Authorization: `Bearer ${token}` },
          });
        }
      }
      console.log('Result(s) saved');
      alert(form.editingId ? 'Result updated successfully' : 'Result(s) added successfully');
      setForm({ student: '', subject: '', score: '', examType: 'opening-term', attendance: 'present', comment: '', editingId: null, type: null });
      const resultsResponse = await axios.get('http://localhost:5000/api/results', {
        params: { school: user?.school || '6826c6741e8bb0ac59a1bea9' },
        headers: { Authorization: `Bearer ${token}` },
      });
      setResults(aggregateResults(resultsResponse.data));
    } catch (error) {
      console.error('Save result error:', error);
      setError(error.response?.data?.error || error.response?.data?.details || 'Failed to save result');
    }
  };

  const handleEdit = (result, subject, field, id) => {
    const newForm = {
      student: result.studentId,
      subject: '',
      score: '',
      examType: 'opening-term',
      attendance: 'present',
      comment: '',
      editingId: null,
      type: null,
    };
    if (field === 'exam') {
      newForm.subject = subject;
      newForm.score = result.subjects[subject][field];
      newForm.examType = field;
      newForm.editingId = result.subjects[subject].ids[field];
      newForm.type = 'exam';
    } else if (field === 'attendance') {
      newForm.attendance = result.attendance;
      newForm.editingId = result.attendanceId;
      newForm.type = 'attendance';
    } else if (field === 'comment') {
      newForm.comment = result.comment;
      newForm.editingId = result.commentId;
      newForm.type = 'comment';
    }
    setForm(newForm);
  };

  const handleDelete = async (id) => {
    try {
      setError('');
      const token = localStorage.getItem('token');
      console.log('Deleting result:', id);
      await axios.delete(`http://localhost:5000/api/results/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log('Result deleted');
      alert('Result deleted successfully');
      const resultsResponse = await axios.get('http://localhost:5000/api/results', {
        params: { school: user?.school || '6826c6741e8bb0ac59a1bea9' },
        headers: { Authorization: `Bearer ${token}` },
      });
      setResults(aggregateResults(resultsResponse.data));
    } catch (error) {
      console.error('Delete result error:', error);
      setError(error.response?.data?.error || error.response?.data?.details || 'Failed to delete result');
    }
  };

  return (
    <div className="page-container">
      <h1>Teacher Dashboard</h1>
      {error && <p className="error">{error}</p>}
      <div className="form-container">
        <h3>{form.editingId ? 'Edit Result' : 'Add Student Results'}</h3>
        <div className="form-group">
          <label htmlFor="student">Student ID</label>
          <input
            type="text"
            name="student"
            value={form.student}
            onChange={handleInputChange}
            placeholder="Student ID"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="subject">Subject</label>
          <input
            type="text"
            name="subject"
            value={form.subject}
            onChange={handleInputChange}
            placeholder="Subject (e.g., Math)"
            disabled={form.type === 'attendance' || form.type === 'comment'}
          />
        </div>
        <div className="form-group">
          <label htmlFor="score">Score (0-100)</label>
          <input
            type="number"
            name="score"
            value={form.score}
            onChange={handleInputChange}
            placeholder="Score (0-100)"
            min="0"
            max="100"
            disabled={form.type === 'attendance' || form.type === 'comment'}
          />
        </div>
        <div className="form-group">
          <label htmlFor="examType">Exam Type</label>
          <select
            name="examType"
            value={form.examType}
            onChange={handleInputChange}
            disabled={form.type === 'attendance' || form.type === 'comment'}
          >
            <option value="opening-term">Opening-Term Exam</option>
            <option value="mid-term">Mid-Term Exam</option>
            <option value="end-term">End-Term Exam</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="attendance">Attendance</label>
          <select
            name="attendance"
            value={form.attendance}
            onChange={handleInputChange}
            disabled={form.type === 'exam' || form.type === 'comment'}
          >
            <option value="present">Present</option>
            <option value="absent">Absent</option>
            <option value="excused">Excused</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="comment">Comment</label>
          <textarea
            name="comment"
            value={form.comment}
            onChange={handleInputChange}
            placeholder="Comment (e.g., Great effort)"
            rows="4"
            disabled={form.type === 'exam' || form.type === 'attendance'}
          />
        </div>
        <button onClick={handleSubmit}>{form.editingId ? 'Update Result' : 'Add Result'}</button>
        {form.editingId && (
          <button
            type="button"
            className="cancel-button"
            onClick={() => setForm({ student: '', subject: '', score: '', examType: 'opening-term', attendance: 'present', comment: '', editingId: null, type: null })}
          >
            Cancel
          </button>
        )}
      </div>
      {results.length > 0 && (
        <div className="form-container">
          <h3>Manage Students</h3>
          <table className="results-table">
            <thead>
              <tr>
                <th>Subject</th>
                <th>Opening-Term Exam</th>
                <th>Mid-Term Exam</th>
                <th>End Term Marks</th>
                <th>Grade</th>
                <th>Rank</th>
                <th>Comment</th>
                <th>Teacher</th>
              </tr>
            </thead>
            <tbody>
              {results.map((result) =>
                Object.entries(result.subjects).map(([subject, data]) => (
                  <tr key={`${result.studentId}-${subject}`}>
                    <td>{subject}</td>
                    <td>
                      {data.openingTerm || '-'}
                      {data.ids['opening-term'] && (
                        <>
                          <button
                            className="edit-button"
                            onClick={() => handleEdit(result, subject, 'opening-term', data.ids['opening-term'])}
                          >
                            Edit
                          </button>
                          <button
                            className="delete-button"
                            onClick={() => handleDelete(data.ids['opening-term'])}
                          >
                            Delete
                          </button>
                        </>
                      )}
                    </td>
                    <td>
                      {data.midTerm || '-'}
                      {data.ids['mid-term'] && (
                        <>
                          <button
                            className="edit-button"
                            onClick={() => handleEdit(result, subject, 'mid-term', data.ids['mid-term'])}
                          >
                            Edit
                          </button>
                          <button
                            className="delete-button"
                            onClick={() => handleDelete(data.ids['mid-term'])}
                          >
                            Delete
                          </button>
                        </>
                      )}
                    </td>
                    <td>
                      {data.endTerm || '-'}
                      {data.ids['end-term'] && (
                        <>
                          <button
                            className="edit-button"
                            onClick={() => handleEdit(result, subject, 'end-term', data.ids['end-term'])}
                          >
                            Edit
                          </button>
                          <button
                            className="delete-button"
                            onClick={() => handleDelete(data.ids['end-term'])}
                          >
                            Delete
                          </button>
                        </>
                      )}
                    </td>
                    <td>{data.grade}</td>
                    <td>{result.rank}</td>
                    <td>
                      {result.comment || '-'}
                      {result.commentId && (
                        <>
                          <button
                            className="edit-button"
                            onClick={() => handleEdit(result, null, 'comment', result.commentId)}
                          >
                            Edit
                          </button>
                          <button
                            className="delete-button"
                            onClick={() => handleDelete(result.commentId)}
                          >
                            Delete
                          </button>
                        </>
                      )}
                    </td>
                    <td>{result.teacher}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Teacher;
