import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Announcements() {
  const [announcements, setAnnouncements] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    const fetchAnnouncements = async () => {
      try {
        setError('');
        setLoading(true);
        // Replace with your school ID
        const schoolId = '6826c6741e8bb0ac59a1bea9';
        const response = await axios.get('http://localhost:5000/api/announcements', {
          params: { school: schoolId },
        });
        console.log('Announcements received:', response.data);
        const sortedAnnouncements = response.data.sort(
          (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
        );
        setAnnouncements(sortedAnnouncements);
      } catch (error) {
        console.error('Fetch announcements error:', error);
        setError(error.response?.data?.error || 'Failed to load announcements');
      } finally {
        setLoading(false);
      }
    };
    fetchAnnouncements();
  }, []);

  const handleFilterChange = (category) => {
    setFilter(category);
  };

  const filteredAnnouncements = filter === 'all'
    ? announcements
    : announcements.filter((ann) => ann.category === filter);

  return (
    <div className="blog-page">
      <div className="hero-section">
        <h1>School News & Updates</h1>
        <p>Stay informed with the latest announcements, news, and activities</p>
      </div>
      <div className="filter-container">
        <button
          className={filter === 'all' ? 'filter-button active' : 'filter-button'}
          onClick={() => handleFilterChange('all')}
        >
          All
        </button>
        <button
          className={filter === 'announcement' ? 'filter-button active' : 'filter-button'}
          onClick={() => handleFilterChange('announcement')}
        >
          Announcements
        </button>
        <button
          className={filter === 'news' ? 'filter-button active' : 'filter-button'}
          onClick={() => handleFilterChange('news')}
        >
          News
        </button>
        <button
          className={filter === 'article' ? 'filter-button active' : 'filter-button'}
          onClick={() => handleFilterChange('article')}
        >
          Articles
        </button>
        <button
          className={filter === 'activity' ? 'filter-button active' : 'filter-button'}
          onClick={() => handleFilterChange('activity')}
        >
          Activities
        </button>
      </div>
      {error && <p className="error">{error}</p>}
      {loading ? (
        <p className="loading">Loading announcements...</p>
      ) : filteredAnnouncements.length === 0 ? (
        <p className="no-content">No announcements available</p>
      ) : (
        <div className="blog-container">
          {filteredAnnouncements.map((announcement, index) => (
            <div
              key={announcement._id}
              className={index === 0 ? 'featured-post' : 'blog-card'}
            >
              {announcement.image && (
                <img
                  src={announcement.image}
                  alt={announcement.title}
                  className="post-image"
                />
              )}
              <div className="post-content">
                <span className="post-category">{announcement.category || 'Announcement'}</span>
                <h3>{announcement.title}</h3>
                <p className="post-summary">
                  {announcement.content.length > 150
                    ? `${announcement.content.substring(0, 150)}...`
                    : announcement.content}
                </p>
                <p className="post-date">
                  Posted: {new Date(announcement.createdAt).toLocaleDateString()}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Announcements;
