import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles.css';

function Announcements() {
  const [announcements, setAnnouncements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAnnouncements = async () => {
      try {
        setError('');
        setLoading(true);
        const schoolId = '6826c6741e8bb0ac59a1bea9';
        console.log('Fetching announcements for school:', schoolId);
        const response = await axios.get('http://localhost:5000/api/announcements', {
          params: { school: schoolId },
        });
        console.log('Announcements received:', response.data);
        setAnnouncements(response.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)));
      } catch (error) {
        console.error('Fetch error:', error);
        setError(error.response?.data?.error || 'Failed to load announcements');
      } finally {
        setLoading(false);
      }
    };
    fetchAnnouncements();
  }, []);

  return (
    <div className="announcements-page">
      <h1>School Announcements</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : announcements.length === 0 ? (
        <p>No announcements available</p>
      ) : (
        <div className="announcements-list">
          {announcements.map((announcement) => (
            <div key={announcement._id} className="announcement-card">
              {announcement.image && (
                <img src={announcement.image} alt={announcement.title} className="announcement-image" />
              )}
              <h3>{announcement.title}</h3>
              <p>{announcement.content}</p>
              <p className="category">{announcement.category || 'Announcement'}</p>
              <p className="date">Posted: {new Date(announcement.createdAt).toLocaleDateString()}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Announcements;
